    public class userAdapter extends RecyclerView.Adapter<userAdapter.userVIEWHOLDER>
    {
        ArrayList<UserModel> userModels;

        public userAdapter(ArrayList<UserModel> userModels)
        {
            this.userModels = userModels;
        }

        @NonNull
        @Override
        public userVIEWHOLDER onCreateViewHolder(@NonNull ViewGroup parent, int viewType)
        {
            View view = LayoutInflater.from(getApplicationContext()).inflate(R.layout.item,null);
            return new userVIEWHOLDER(view);
        }

        @Override
        public void onBindViewHolder(@NonNull userVIEWHOLDER holder, int position)
        {
          UserModel model = userModels.get(position);
          holder.txtCrepe.setText(model.getCrepe());
          holder.txtMac.setText(model.getMac());
          holder.txtPizza.setText(model.getPizza());
          holder.txtFood.setText(model.getFood());

          holder.imageCrepe.setImageResource(R.drawable.ic_crepe);
          holder.imageMac.setImageResource(R.drawable.ic_chicken_hand_drawn_outline);
          holder.imagePizza.setImageResource(R.drawable.ic_pizza_slice);
          holder.imageFood.setImageResource(R.drawable.ic_restaurant);
        }

        @Override
        public int getItemCount()
        {
            return userModels.size();
        }

        public class userVIEWHOLDER extends RecyclerView.ViewHolder
        {
            TextView txtCrepe,txtMac,txtPizza,txtFood;
            ImageView imageCrepe,imageMac,imagePizza,imageFood;
            public userVIEWHOLDER(@NonNull View itemView)
            {
                super(itemView);

                txtCrepe = itemView.findViewById(R.id.txt_city);
                txtMac = itemView.findViewById(R.id.txt_mac);
                txtPizza = itemView.findViewById(R.id.txt_hut);
                txtFood = itemView.findViewById(R.id.txt_tareq);

                imageCrepe = itemView.findViewById(R.id.img_city);
                imageMac = itemView.findViewById(R.id.img_mac);
                imagePizza = itemView.findViewById(R.id.img_hut);
                imageFood = itemView.findViewById(R.id.img_tareq);
            }
        }
    }
